export function openModal(modalName){this.elements.modalOverlay.classList.remove("hidden");document.querySelectorAll(".modal").forEach((modal)=>{modal.classList.add("hidden");});document.getElementById(`${modalName}-modal`).classList.remove("hidden");}
export function closeModal(){this.elements.modalOverlay.classList.add("hidden");document.querySelectorAll(".modal").forEach((modal)=>{modal.classList.add("hidden");});}
export function showNotification(message,type="info"){const container=document.getElementById("notifications");const notification=document.createElement("div");notification.className=`notification ${type}`;notification.textContent=message;container.appendChild(notification);setTimeout(()=>{notification.style.animation="slideOut 0.3s ease";setTimeout(()=>notification.remove(),300);},5000);}
export function showMessageModal(message,title="SYSTEM MESSAGE",onConfirm=null,onCancel=null){const modal=document.getElementById("message-modal");const titleEl=document.getElementById("message-title");const contentEl=document.getElementById("message-content");const closeBtn=document.getElementById("message-close-btn");titleEl.textContent=title;contentEl.textContent=message;document.getElementById("modal-overlay").classList.remove("hidden");modal.classList.remove("hidden");const closeModal=()=>{document.getElementById("modal-overlay").classList.add("hidden");modal.classList.add("hidden");if(onCancel)onCancel();};const handleConfirm=()=>{closeModal();if(onConfirm)onConfirm();};const handleCancel=()=>{closeModal();if(onCancel)onCancel();};closeBtn.addEventListener("click",handleConfirm);document.querySelectorAll(".modal-close").forEach((btn)=>{btn.addEventListener("click",handleCancel);});document.getElementById("modal-overlay").addEventListener("click",(e)=>{if(e.target===document.getElementById("modal-overlay")){handleCancel();}});closeBtn.focus();}